            for (int i = 0; i < xpos.Length; i++)
            {
                GameObject temp = Instantiate(house, new Vector3((float)xpos[i], 0f, (float)Ypos[i]), Quaternion.Euler(0, Random.Range(0, 359), 0), GameObject.Find("houses").transform);
